$(document).click((function(t){let e=$(t.target).closest("[data-toggle-class]").attr("data-toggle-class");if(!e)return;let l=e.split(",");$.each(l,(function(t,e){$("."+e).toggleClass(e+"--open")}))})),$(document).click((function(t){let e=$(t.target).closest("[data-scrollto]").attr("data-scrollto");if(!e)return;let l=$(e);$("html, body").animate({scrollTop:l.offset().top-50},1e3)})),$(document).ready((function(){$(".reviews__slider-inner").slick({nextArrow:$(".reviews__slider-btn--next"),prevArrow:$(".reviews__slider-btn--prev")})}));